import { createGlobalStyle } from 'styled-components';

export const GlobalStyles = createGlobalStyle`
body {
    font-family: 'Quicksand';
    padding: 20px 60px;
}

a {
    color: black;
    text-decoration: none;
}

* {
    box-sizing: border-box;
}
`
